export const Outfit = ({ top, bottom, shoes }) => {
  return (
    <div className="outfit">
      {top && bottom && shoes && (
        <>
          <h3>TOP</h3>
          <img src={top.imageUrl}></img>
          <p>{top.description}</p>
          <h4>BOTTOM</h4>
          <img src={bottom.imageUrl}></img>
          <p>{bottom.description}</p>
          <h4>SHOES</h4>
          <img src={shoes.imageUrl}></img>
          <p>{shoes.description}</p>
        </>
      )}
    </div>
  );
};

export const OutfitStyle = ({ newOutfit }) => {
  return (
    <div className="styles">
      <h3>Fashion Styles / Dress Code</h3>
      <button onClick={() => newOutfit("casual")}>Casual</button>
      <button onClick={() => newOutfit("sport")}>Sport</button>
      <button onClick={() => newOutfit("formal")}>Formal</button>
    </div>
  );
};
